import React from 'react';
import './Order.scss'
import { FaXmark } from "react-icons/fa6";
import { FaChevronLeft } from "react-icons/fa6";
import { FaChevronRight } from "react-icons/fa6";

const Order = ({item, onDelete, onDecrease, onIncrease}) => {
    const {id, title, desc, category, count, price, img} = item

    return (
        <div className='item2'>
            <div className="img"><img src={img} alt={title}/></div>
            <div>
                <h3>{title}</h3>
                <b>{price}$</b>
            </div>
            <div className="count">
                <div onClick={() => onDecrease(item)}>-</div>
                <b>{count}</b>
                <div onClick={() => onIncrease(item)}>+</div>
            </div>
            <div className="remove">

                <FaXmark onClick={()=>onDelete(id)} />
            </div>
        </div>
    );
};

export default Order;
