import React, {useState} from 'react';
import Item from "./Item";
import './Items.scss';

const Items = ({items, onAdd, onShowItem}) => {
    const itemsPerPage = 10; // Кількість елементів на сторінці
    const [currentPage, setCurrentPage] = useState(1);

    // Визначте загальну кількість сторінок
    const totalPages = Math.ceil(items.length / itemsPerPage);

    // Функція для зміни поточної сторінки
    const handlePageChange = (pageNumber) => {
        setCurrentPage(pageNumber);
    };

    // Визначте початковий та кінцевий індекси для елементів на поточній сторінці
    const startIndex = (currentPage - 1) * itemsPerPage;
    const endIndex = startIndex + itemsPerPage;

    // Отримайте підмасив даних для поточної сторінки
    const currentData = items.slice(startIndex, endIndex);

    return (
        <>
            <main>
                {/* Відображайте дані для поточної сторінки */}
                {currentData.map((item) => (
                    <Item key={item.id} item={item} onAdd={onAdd} onShowItem={onShowItem}/>
                ))}

                {/* Відображайте навігацію з посиланнями на сторінки */}
            </main>
            <div className="pagination_wrapper">
                <ul className="pagination">
                    {Array.from({length: totalPages}, (_, index) => index + 1).map((page) => (
                        <li key={page}>
                            <button
                                className={currentPage === page ? "active" : ""}
                                onClick={() => handlePageChange(page)}
                            >
                                {page}
                            </button>
                        </li>
                    ))}
                </ul>
            </div>
        </>
    )
        ;
};

export default Items;
